svg.script:
const svg=document.querySelector('#animation');
const frames=Array.from(svg.children).filter(child=> child.tagName==='g');
const numFrames=frames.length;
const animationDuration=800; // Animation duration in milliseconds

// Generate keyframes for the animation
const keyframesRule=document.styleSheets[0].cssRules[0].appendRule(` @keyframes animate {
        $ {
            Array.from({
                length: numFrames
            }

            , (_, i)=> ` $ {
                (i / (numFrames - 1)) * 100
            }

            % {
                opacity: $ {
                    i % 2===0 ? 1 : 0
                }

                ;
            }

            `).join('')
    }
}

`);

// Add frames to the SVG
frames.forEach((frame, i)=> {
        frame.id=`frame-$ {
            i + 1
        }

        `;
        frame.style.opacity='0';

        frame.style.animation=`animate $ {
            animationDuration
        }

        ms infinite $ {
            (i / (numFrames - 1)) * animationDuration
        }

        ms`;
    });
